import axios from "axios";
import React, { Component } from "react";
import ourLogo from "./assets/images/logo.png";
import "./App.css";

class App extends Component {
  state = {
    // Initially, no file is selected
    selectedFile: null,
  };

  // On file select (from the pop up)
  onFileChange = (event) => {
    // Update the state
    this.setState({ selectedFile: event.target.files[0] });
  };

  // On file upload (click the upload button)
  onFileUpload = () => {
    // Create an object of formData
    const formData = new FormData();

    // Update the formData object
    formData.append(
      "myFile",
      this.state.selectedFile,
      this.state.selectedFile.name
    );

    // Details of the uploaded file
    console.log(this.state.selectedFile);

    // Request made to the backend api
    // Send formData object
    console.log(formData.values());
    axios.post("http://localhost:8080/files", formData);
  };

  // File content to be displayed after
  // file upload is complete
  fileData = () => {
    if (this.state.selectedFile) {
      console.log(this.state.selectedFile);
      return (
        <div>
          <h2>File Details:</h2>

          <p>File Name: {this.state.selectedFile.name}</p>

          <p>File Type: {this.state.selectedFile.type}</p>

          <p>
            Last Modified:{" "}
            {this.state.selectedFile.lastModifiedDate &&
              this.state.selectedFile.lastModifiedDate.toDateString()}
          </p>
        </div>
      );
    } else {
      return (
        <div>
          <br />
          <h4>Choose before Pressing the Upload button</h4>
        </div>
      );
    }
  };

  render() {
    return (
      <div className="background">

        <header className="nav">
          <img className="nav__logo" src={ourLogo} alt="our logo"/>
          <h1 className="nav__title white-text">Web Eyes</h1> 
        </header>

        <div className="sections">
              <div className="sections__cards">
                <h3 className="white-text">Web eyes - upload test</h3>
                <div>
                  <input type="file" onChange={this.onFileChange} />
                  <button onClick={this.onFileUpload}>Upload!</button>
                </div>
                {this.fileData()}

              </div>

              <div className="sections__cards white-text">
                  Uploaded image will go here

              </div>

              <div className="sections__cards">
                  Image tags section will be going here
                
              </div>
        </div>

      </div>
    );
  }
}

export default App;
